mapper.update:
update tb_user
user_pwd = #{userPwd}, user_name = #{userName}, gender = #{gender}, email = #{email}, room_id = #{roomId}, user_type = #{userType},
where id = #{id}

sql.where:
user_type != 2
and id like concat('%', #{id},'%')
and user_pwd = #{userPwd}
and user_name like concat('%', #{userName},'%') 
and gender = #{gender}
and email = #{email}
and room_id = #{roomId}
and user_type = #{userType}
